var QuickView={createItem:function(a,b){return $("<"+a+">").prop("id",b)
},removeItem:function(b,a){$("#"+a).children().remove("#"+b)
},append:function(a,b,d){var c=QuickView.createItem(a,b);
$("#"+d).append(c)
},prepend:function(a,b,d){var c=QuickView.createItem(a,b);
$("#"+d).prepend(c)
},scrollToBottom:function(b){var a=$("#"+b);
scrollTo(b,$(a).height())
},scrollToTop:function(a){scrollTo(a,0)
},scrollTo:function(c,a){var b=$("#"+c);
$(b).scrollTop(a)
},isComponentScrollBarAtBottom:function(b){var a=$("#"+b);
return($(a).prop("offsetHeight")+$(a).scrollTop()>=$(a).prop("scrollHeight"))
},isPageScrollBarAtBottom:function(){return(($(window).height()+$(window).scrollTop())>=$(document).height())
}};